import React, {useState} from 'react'

function MeetingLenMenu(props: { meetingLenMenu: boolean,
    meetingLenCallback(meetingLen: number | undefined):void,
    tempMeeting: number | undefined, toggleMeetingLenMenu:()=>void
    }) {
    const [tempMeeting, setTempMeeting] = useState<number | undefined>(props.tempMeeting);

    const handleForm = (e: any) => {
        if (e.key === 'Enter') {
            e.preventDefault();
            props.meetingLenCallback(tempMeeting);
        }
    }
    const handleFormOnSubmit = (e: any) => {
        e.preventDefault();
        props.meetingLenCallback(tempMeeting);
    }

    return (
        <form className="meetingLenForm" onSubmit={handleFormOnSubmit}
              style={{display: !props.meetingLenMenu ? 'none' : ''}}>
            <label> Meeting Length:
                <input className="inputMeetingLen" onKeyDown={handleForm}
                       onSubmit={handleFormOnSubmit}
                       onChange={(e: any) => setTempMeeting(e.target.value)}
                       value={props.tempMeeting || ''}
                       id='meetingLen'/> min
            </label>
            <button className="buttonStyle" disabled={props.tempMeeting === undefined}
                    type='submit'>Submit
            </button>
            <button className="xOutMeetingLen" onClick={props.toggleMeetingLenMenu}>x</button>
        </form>)
}
export default MeetingLenMenu
