import React from 'react'
import { useState, useEffect } from 'react';
import './Timer.css'

const Timer = (props:{ initialMinute: number, active: boolean }) => {
    const [minutes, setMinutes] = useState(0);
    useEffect(() => {
        let myInterval = setInterval(() => {
            if (minutes < props.initialMinute && props.active) {
                setMinutes(minutes + 1);
            }
        }, 1000)
        return () => {
            clearInterval(myInterval);
        };
    });

    return (
        <div className="timer">
            { minutes === 0
                ? null
                : <h1> {minutes} min </h1>
            }
        </div>
    )
}

export default Timer;
