import React, {useEffect, useState} from 'react'
import { Draggable } from 'react-beautiful-dnd'
import {getLongestName} from "../API";
import './TodoItem.css'
import Timer from "./Timer";

type Props = TodoProps & {
    updateTodo: (todo: ITodo) => void
    deleteTodo: (_id: string) => void
    index: number
    active: boolean
    done: boolean
}
const Todo: React.FC<Props> = ({ todo, active, done, index }) => {
    const [state, setState] = useState<number>(0);
    const myCallback = (dataFromChild: number) => {
        setState(dataFromChild);
    }
    const [longest, setLong] = useState<number>(0);
    const [color, setColor] = useState<string>('turquoise');
    useEffect(() => {
        handleColor()
    },)
    const handleColor = (): void => {
        if(todo.time <= state){
            setColor('magenta');
        }
    }
    useEffect(() => {
        handleLongest()
    },)
    const handleLongest = (): void => {
        getLongestName()
            .then(({ data: { longest} }:number|any) => setLong(longest))
            .catch((err: Error) => console.log(err))
    }

    return (
      <Draggable draggableId={todo._id} index={index} isDragDisabled={done || active}>
          {provided => (
    <div className='Card' ref={provided.innerRef}
         {...provided.draggableProps}
         {...provided.dragHandleProps}>
      <div className="Card--text"
           style={{
               transitionProperty: 'background-position',
               transitionTimingFunction: 'linear',
               transitionDuration: todo.time*60/60+'s',
               backgroundPosition: active ? '0%': '100%',
               textDecoration: done ? 'line-through' : 'none',
                }}>
        <div className='name'
             style={{width: longest*6.5 + "px", backgroundColor: done ? color: 'none' }} >
            {todo.name} </div>
        <div className='description'  style={{paddingBottom: todo.time/3 + '%',
            backgroundColor: done ? 'rgba(230, 230, 230, 1)': 'none'
        }}>{todo.description}</div>
          <div className="time" style = {{backgroundColor: done ? 'rgba(230, 230, 230, 1)': 'none'}}>
              <div className="set-time">
                  {todo.time} min</div>
              <Timer callbackFromParent={myCallback} initialMinute = {todo.time} active = {active}  />

          </div>
      </div>
    </div>
          )}
      </Draggable>
  )
}

export default Todo
