import React from 'react'
import { useState, useEffect } from 'react';

const Timer = (props:{ active: boolean, done: boolean,
    callbackFromParent(listInfo: number): void;
}) => {
    const [seconds, setSeconds] = useState(0);
    const [milseconds, setMilSeconds] = useState(0);
    // useEffect(() => {
    //     let myInterval = setInterval(() => {
    //         if (props.active) {
    //                 setSeconds(seconds+1)
    //         }
    //     }, 1000)
    //     return () => {
    //         clearInterval(myInterval);
    //     };
    // });
    useEffect(() => {
        let myInterval = setInterval(() => {
            if (props.active) {
                setMilSeconds(milseconds+1)
                if(milseconds===1000){
                    setSeconds(seconds+1)
                }
            }
        },1 )
        return () => {
            clearInterval(myInterval);
        };
    });
    const sendSeconds = () => {
        props.callbackFromParent(seconds);
    }
    useEffect(() => {
        sendSeconds()
    },)
    return (
        <div className="timer" style={{fontSize: "10px", marginTop: '-2%'}}>
            {props.active || props.done ? <div>
                    {seconds} min </div> :
                <div> - min </div>
            }
        </div>
    )
}


export default Timer;
