import React from 'react'
import { Draggable } from 'react-beautiful-dnd'
import fs from 'fs';
const pathFull = "C:\\Users\\Saralin\\IdeaProjects\\fullstack-typescript-mern-todo\\Meeting\\"

type Props = TodoProps & {
    updateTodo: (todo: ITodo) => void
    deleteTodo: (_id: string) => void
    index: number
}
 // const arr = ['hello', 'string', 'asdasd'];
const arr = fs.readdirSync(pathFull);
const Todo: React.FC<Props> = ({ todo, index }) => {
    const longest = arr.reduce(

        function (a, b) {
            return a.length > b.length ? a : b;
        }
    );

  return (
      <Draggable draggableId={todo._id} index={index} isDragDisabled={todo.status}>
          {provided => (
    <div className='Card' ref={provided.innerRef}
         {...provided.draggableProps}
         {...provided.dragHandleProps}>
      <div className="Card--text" >
        <div className='name' > {longest} {todo.name}</div>
        <div className='description'>{todo.description}</div>
      </div>
    </div>
          )}
      </Draggable>
  )
}

export default Todo
