import React from 'react'
import { useState, useEffect } from 'react';


const Timer = (props:{ initialMinute: number, active: boolean, done: boolean, realMinute: number
}) => {
    const [seconds, setSeconds] = useState(0);
    onChange = setSeconds()
    useEffect(() => {
        onChange(props.realMinute)
        let myInterval = setInterval(() => {
            if (props.active) {
                setSeconds(props.realMinute);
            }
        }, 1)
        return () => {
            clearInterval(myInterval);
        };
    });
    // const someFn = () => {
    //     props.callbackFromParent(seconds);
    // }
    // useEffect(() => {
    //     someFn()
    // },)
    return (
        <div className="timer" style={{fontSize: "x-small"}}>
            {props.active || props.done ? <div> {seconds} min </div> : <div> - min </div>
            }
        </div>
    )
    }


export default Timer;
