import React, { useState } from 'react'
// import Select from 'react-select';

type Props = { 
  saveTodo: (e: React.FormEvent, formData: ITodo | any) => void 
}

const AddTodo: React.FC<Props> = ({ saveTodo }) => {
  const [options] = React.useState([
    { value: '5' , label: "5 min", id: 'time'},
    { value: '10', label: '10 min', id: 'time' },
    { value: '15', label: '15 min', id:'time' },
  ]);
  const [formData, setFormData] = useState<ITodo | {}>()
  const [name, setName] = useState<string>();
  const [description, setDescription] = useState<string>()
  const [time, setTime] = useState<string>();

  const handleForm = (e: React.FormEvent<HTMLInputElement>): void => {
    if(e.currentTarget.id === 'name'){
      setName(e.currentTarget.value)}
    else if(e.currentTarget.id === 'description'){
      setDescription(e.currentTarget.value)}
    else{
    // else{
      setTime('5');
    }
  // }
    setFormData({
      ...formData,
      [e.currentTarget.id]: e.currentTarget.value,
    })
  }

  return (
    <form className='Form' onSubmit={(e) => saveTodo(e, formData)}>
      <span>{time}</span>
      <div>
        <div>
          <label htmlFor='name'>Name: </label>
          <input className = "nameInput" value = {name} onChange={handleForm} type='text' id='name' />
        </div>
        <div>
          <label htmlFor='description'>Description: </label>
          <input onChange={handleForm} value = {description} type='text' id='description' />
        </div>
      </div>
      <div>
        <label htmlFor='time'>Time: </label>
        <select>
        {options.map(item => (
            <option
                key={item.value}
                value={item.value}
            >
              {item.label}
            </option>
        ))}
          </select>
        </div>
      <span> {time}</span>
      <button disabled={name === undefined || description === undefined || time === undefined} >Add Slot</button>
    </form>
  )
}

export default AddTodo
