import React, {useState} from 'react'
import './BonusItem.css'
import Timer from "./Timer";
// import Timer from "./Timer";

function BonusItem(props: { active: boolean, done: boolean }){
    const [, setTime] = useState<number>(0);
    const myCallback = (dataFromChild: number) => {
            setTime(dataFromChild);
        }
        return(
        <div className="bottom-panel" style = {{
            transitionDuration: 8+'s',
            backgroundPosition: props.active ? '0%': '100%',
            background: props.done ? 'rgb(102,230,217)':'',
            textDecoration: props.done ? 'line-through' : 'none',}}>
            <div className="bonus" style={{paddingBottom:8/2 + '%',
                transition: 8+'s'}}> Bonus Time </div>
            <div className="bonus-time">
                <div className="set-time">
                    8 min</div>
                <Timer callbackFromParent={myCallback} initialMinute = {8} active = {props.active}  />
            </div>
        </div>
        )}


export default BonusItem
