import React from 'react'
import { useState, useEffect } from 'react';
import Timer from "react-compound-timer";

const MyTimer = (props:{ active: boolean, done: boolean,
    callbackFromParent(listInfo: number): void
    // callbackFromParentMil(listInfo: number): void;
}) => {
    const [seconds, setSeconds] = useState(0);
    // const [milSeconds, setMilSeconds] = useState(0);

    useEffect(() => {
        let secondInterval = setInterval(() => {
            if (props.active) {
                setSeconds(seconds + 1)
            }
        }, 1000)
        return () => {
            clearInterval(secondInterval);
            // clearInterval(milInterval);
        };
    });
    // useEffect(() => {
    //     let milInterval = setInterval(() => {
    //         if (props.active) {
    //             setMilSeconds(milSeconds + 1)
    //             if (milSeconds == 100) {
    //                 setSeconds(seconds + 1)
    //                 setMilSeconds(0)
    //             }
    //         }
    //     }, 10)
    //     return () => {
    //         clearInterval(milInterval);
    //     };
    // });
    const sendSeconds = () => {
        props.callbackFromParent(seconds);
    }
    useEffect(() => {
        sendSeconds()
    },)
    // const sendMilSecond = () => {
    //     props.callbackFromParentM(milSeconds);
    // }
    // useEffect(() => {
    //     sendMilSecond()
    // },)
    return (
        <div className="timer" style={{fontSize: "10px", marginTop: '-2%'}}>
            {props.active || props.done ? <div>
                    <Timer
                        initialTime={55000}
                    >
                        {({ start:any, resume, pause, stop, reset, timerState }) => (
                            <React.Fragment>
                                <div>
                                    <Timer.Days /> days
                                    <Timer.Hours /> hours
                                    <Timer.Minutes /> minutes
                                    <Timer.Seconds /> seconds
                                    <Timer.Milliseconds /> milliseconds
                                </div>
                                <div>{timerState}</div>
                                <br />
                                <div>
                                    <button onClick={start}>Start</button>
                                    <button onClick={pause}>Pause</button>
                                    <button onClick={resume}>Resume</button>
                                    <button onClick={stop}>Stop</button>
                                    <button onClick={reset}>Reset</button>
                                </div>
                            </React.Fragment>
                        )}
                    </Timer>
                </div> :
                <div> - min </div>
            }
        </div>
    );
}

export default MyTimer;
