import React from 'react'
import { useState, useEffect } from 'react';

const MyTimer = (props:{ active: boolean, done: boolean,
    callbackFromParent(listInfo: number): void
    // callbackFromParentMil(listInfo: number): void;
}) => {
    const [time, setTime] = useState(0);
    const [seconds, setSeconds] = useState(0);

    useEffect(() => {
        let myInterval: any = null;
        if (props.active) {
            myInterval = setInterval(() => {
                setTime((time) => time + 10);
            }, 10);
            if(time === 1000){setSeconds(seconds + 1);
            setTime(0)}
        } else {
            clearInterval(myInterval);
        }
        return () => {
            clearInterval(myInterval);
        };
    });
    const someFn = () => {
        props.callbackFromParent(Math.floor((time / 1000) % 60));
    }
    useEffect(() => {
        someFn()
    },)
    return (
        <div className="timer" style={{
            fontSize: "10px", marginTop: '-2%',
        }}>
            {props.active || props.done ? <div> {("0" + Math.floor((time / 1000) % 60)).slice(-2)}
                </div> :
                <div> - min </div>
            }
        </div>
    );
}

export default MyTimer;
