import React, {useEffect, useState} from 'react'
import { Draggable } from 'react-beautiful-dnd'
import {getLongestName} from "../API";
import './TodoItem.css'
import Timer from "./Timer";

type Props = TodoProps & {
    updateTodo: (todo: ITodo) => void
    deleteTodo: (_id: string) => void
    index: number
    active: boolean
    done: boolean
}
const Todo: React.FC<Props> = ({ todo, active, done, index }) => {
    const [realTime, setTime] = useState<number>(0);
    const myCallback = (dataFromChild: number) => {
        setTime(dataFromChild);
    }
    const [longest, setLong] = useState<number>(0);
    const [color, setColor] = useState<string>('rgb(160,240,232)');
    useEffect(() => {
        handleColor()
    },)
    const handleColor = (): void => {
        const diff = realTime-todo.time
        if(Math.abs(diff) <= 1){
            setColor('rgb(160,240,232)');
        }
        else if(diff > 4){
            setColor('rgb(255,125,255)');
        }
        else if(diff > 1){
            setColor('rgb(255,202,255)');
        }
    }
    useEffect(() => {
        handleLongest()
    },)
    const handleLongest = (): void => {
        getLongestName()
            .then(({ data: { longest} }:number|any) => setLong(longest))
            .catch((err: Error) => console.log(err))
    }

    return (
      <Draggable draggableId={todo._id} index={index} isDragDisabled={done || active}>
          {provided => (
    <div className='Card' ref={provided.innerRef}
         {...provided.draggableProps}
         {...provided.dragHandleProps}>
      <div className= {(realTime<todo.time) ? "Card--text": "Card--reverse"}
          style={{
               transitionDuration: (realTime<todo.time) ?
                   (todo.time-realTime) +'s' : todo.time+'s',
               backgroundPosition: (realTime<todo.time) && active ? '0% 100%': '100% 0%',
              textDecoration: done ? 'line-through' : 'none',
           }}>
        <div className='name'
             style={{width: longest*6.5 + "px", backgroundColor: done ? color: '' }} >
            {todo.name} </div>
        <div className='description'  style={{paddingBottom: realTime + 'px',
            backgroundColor: done ? 'rgba(230, 230, 230, 1)': ''
        }}>{todo.description}</div>
          <div className="time" style = {{backgroundColor: done ? 'rgba(230, 230, 230, 1)': ''}}>
              <div className="set-time" >
                  {todo.time} min</div>
              <Timer callbackFromParent={myCallback} initialMinute = {todo.time} active = {active}  />

          </div>
      </div>
    </div>
          )}
      </Draggable>
  )
}

export default Todo
