import React from 'react'
import { Draggable } from 'react-beautiful-dnd'
type Props = TodoProps & {
    updateTodo: (todo: ITodo) => void
    deleteTodo: (_id: string) => void
    index: number
}

const Todo: React.FC<Props> = ({ todo, updateTodo, deleteTodo, index }) => {
  const checkTodo: string = todo.status ? `line-through` : ''
  return (
      <Draggable draggableId={todo._id} index={index}>
          {provided => (
    <div className='Card' ref={provided.innerRef}
         {...provided.draggableProps}
         {...provided.dragHandleProps}>
      <div className="Card--text" >
        <div className={checkTodo} > {todo.name}</div>
        <div className={checkTodo}>{todo.description}</div>
      </div>
      <div className='Card--button'>
        <button
          onClick={() => updateTodo(todo)}
          className={todo.status ? `hide-button` : 'Card--button__done'}
        >
          Complete
        </button>
        <button
          onClick={() => deleteTodo(todo._id)}
          className='Card--button__delete'
        >
          Delete
        </button>
      </div>
    </div>
          )}
      </Draggable>
  )
}

export default Todo
