import React from 'react'
import { useState, useEffect } from 'react';


const Timer = (props:{ initialMinute: number, active: boolean,
    callbackFromParent(listInfo: number): void;
}) => {
    const [seconds, setSeconds] = useState(0);

    useEffect(() => {
        let myInterval = setInterval(() => {
            if (props.active) {
                setSeconds(seconds+1);
            }
        }, 10)
        return () => {
            clearInterval(myInterval);
        };
    });
    const someFn = () => {
            props.callbackFromParent(seconds);
        }
    useEffect(() => {
        someFn()
    },)
    return (
        <div className="timer" style = {{fontSize : "x-small"}}>
            { !props.active ? '-min':''}
            { seconds*60 === 0 && props.active
                ? '0 min'
                : <div> {seconds*60} min </div>
            }
        </div>
    )
}

export default Timer;
