import React from 'react'
import Header from "./Header";
import {Droppable} from "react-beautiful-dnd";
import Presenter from "./Presenter";
import Bonus from "./Bonus";

function PresenterList(props: { meetingLen: number, isAdmin: boolean, pres: IPresenter[] },
                       cursor: number, bonusTime: number, origBonus: number, totTime: number,
                       bonusActive: boolean, bonusDone: boolean,
                       closeMenu:()=>void,
                       timeCallback:()=>void,
                       handleDeletePres:()=>void ) {

    let SETTINGS_WIDTH = '3.2%'; //width of settings button
    //returns height (in percent) of presenter's slot
    const getHeight = (presenter: IPresenter): number => {
        let height = (presenter.time - presenter.extra + presenter.overtime);
        height = height / (totTime + bonusTime) * 100
        if (height < 6.5) {
            return (6.5); //minimum height
        }
        return height;
    }
    function getLongestName() {
        let longest = '';
        for (let i = 0; i < props.pres.length; i++) {
            if (props.pres[i].name.length >= longest.length) {
                longest = props.pres[i].name;
            }
        }
        return longest.length;
    }
    return (
        <div className='presWrapper' style={{marginRight: SETTINGS_WIDTH}} onClick={closeMenu}>
            <Header meetingLen={props.meetingLen}/>
            <Droppable droppableId='col-1' isDropDisabled={!props.isAdmin}>
                {provided => {
                    return (
                        <ul className="pres"
                            {...provided.droppableProps} ref={provided.innerRef}>
                            {props.pres.map((presenter: IPresenter, index) => (
                                <Presenter
                                    key={presenter._id} presenter={presenter} index={index}
                                    active={index === cursor} done={index < cursor}
                                    height={getHeight(presenter)} bonusTime={bonusTime}
                                    callbackFromParent={timeCallback}
                                    longestName={getLongestName()}
                                    deletePresApp={handleDeletePres}
                                    admin={props.isAdmin}
                                />
                            ))}
                            {provided.placeholder}
                            <Bonus
                                origBonus={origBonus} time={bonusTime}
                                active={bonusActive} done={bonusDone}
                                height={(bonusTime) / (totTime + bonusTime) * 100}/>
                        </ul>)
                }}
            </Droppable>
        </div>)
}

export default PresenterList
