import React, { useState } from 'react'

type Props = { 
  saveTodo: (e: React.FormEvent, formData: ITodo | any) => void 
}

const AddTodo: React.FC<Props> = ({ saveTodo }) => {
  const [options] = React.useState([
    { label: ""},
    { value: 5 , label: "5 min", id: 'time'},
    { value: 10, label: '10 min', id: 'time' },
    { value: 15, label: '15 min', id:'time' },
  ]);
  const [formData, setFormData] = useState<ITodo | {}>()
  const [name, setName] = useState<string>();
  const [description, setDescription] = useState<string>()
  const [time, setTime] = useState<number>();

  const handleInput = (e: React.FormEvent<HTMLInputElement>): void => {
    if(e.currentTarget.id === 'name'){
      setName(e.currentTarget.value)
      setFormData({
        ...formData,
        [e.currentTarget.id]: e.currentTarget.value,
      })}
    else if(e.currentTarget.id === 'description'){
      setDescription(e.currentTarget.value)
      setFormData({
        ...formData,
        [e.currentTarget.id]: e.currentTarget.value,
      })}
    else{
      setTime(e.currentTarget.valueAsNumber)
      setFormData({
        ...formData,
        ["time"]: e.currentTarget.valueAsNumber,
      })
    }
  }
  const handleOption = (e: React.ChangeEvent<HTMLSelectElement>): void => {
    setTime(parseInt(e.target.value, 10));
    setFormData({
      ...formData,
      ["time"]: parseInt(e.target.value, 10),
    })
  }

    return (
    <form className='Form' onSubmit={(e) => saveTodo(e, formData)}>
      <span>{time}</span>
        <div>
          <label htmlFor='name'>Name: </label>
          <input className = "nameInput" value = {name} onChange={handleInput} type='text' id='name' />
        </div>
        <div>
          <label htmlFor='description'>Description: </label>
          <input onChange={handleInput} value = {description} type='text' id='description' />
        </div>
        <label htmlFor='time'>Time: </label>
        <select onChange={handleOption}>
        {options.map(item => (
            <option
                key={item.value}
                value={item.value}
            >
              {item.label}
            </option>
        ))}
          </select>
      <button disabled={name === undefined || description === undefined || time === undefined} >Add Slot</button>
    </form>
  )
}

export default AddTodo
