import React, {useEffect, useState} from 'react'
import TodoItem from './components/TodoItem'
import {DragDropContext, Droppable, DropResult} from 'react-beautiful-dnd'
import {getTodos,} from './API'
import BonusItem from "./components/BonusItem";
// @ts-ignore
import audio from './boop.mp3';
const useKeyPress = function(targetKey: string) {
    const [keyPressed, setKeyPressed] = useState(false);

    function downHandler({ key }: { key: string }) {
        if (key === targetKey) {
            setKeyPressed(true);
        }
    }
    const upHandler = ({ key }: { key: string }) => {
        if (key === targetKey) {
            setKeyPressed(false);
        }
    };

    React.useEffect(() => {
        window.addEventListener("keydown", downHandler);
        window.addEventListener("keyup", upHandler);

        return () => {
            window.removeEventListener("keydown", downHandler);
            window.removeEventListener("keyup", upHandler);
        };
    });

    return keyPressed;
};

const App: React.FC = () => {
    const downPress = useKeyPress("ArrowDown");
    const upPress = useKeyPress("ArrowUp");

    //time used to minimize the bonus time (from people going overtime)
    const [bonusLost, setBonusLost] = useState<number>(0);

    const [todos, setTodos] = useState<ITodo[]>([]);
    const [selected, setSelected] = useState<ITodo>();
    const [before, setBefore] = useState<ITodo>();

    const [cursor, setCursor] = useState<number>(-1);
    // const [totalOver, setOver] = useState<number>(0);

    const [realTime, setTime] = useState<number>(0);
    const [nonZeroTime, setZero] = useState<number>(0);

    let origBonus = 0;
    const [bonusTime, setBonus] = useState<number>(origBonus);

    const timeCallback = (timerTime: number) => {
        if(timerTime != 0){setZero(timerTime)}
        setTime(timerTime);
    }
    useEffect(() => {
        if (selected !== undefined) {
            //if person goes overtime
            if (realTime > Math.round(selected.time - selected.extra)) {
                //increase selected.overtime so their box increases in size
                selected.overtime = (realTime - Math.round(selected.time - selected.extra));
                //decrease other slots if bonusTime == 0
                if (bonusTime < 1) {
                    let reducedSlot = cursor + selected.overtime -bonusLost;
                    while(reducedSlot >= todos.length ){reducedSlot -= todos.length-cursor-1}
                    todos[reducedSlot].extra += 1;
                } else{ //decrease bonusTime
                    setBonusLost(bonusLost+1);
                    setBonus(bonusTime - 1)
                }
            }
        }
    })
    const getTodoTime = (): number => {
        let todoTime = 0;
        for (let i = 0; i < todos.length; i++) {
            todoTime += todos[i].time;
        }
        return todoTime;
    }
    // const getTotalOver = (): number => {
    //     let totalOver = 0;
    //     for (let i = 0; i < todos.length; i++) {
    //         totalOver += todos[i].overtime;
    //     }
    //     return totalOver;
    // }

    const getPercent = (todo: ITodo): number => {
        let percent = (todo.time + todo.overtime - todo.extra) / (todoTime) * 100
        if(percent<(5/(todoTime) * 100)){
            return (3/(todoTime) * 100);
        }
        return percent;
    }

    const isSlotDecreased = (): boolean => {
        for (let i = cursor + 1; i < todos.length; i++) {
            if(todos[i].extra>0){
                return true;
            }
        }
        return false;
    }
    let todoTime = getTodoTime();
    // let totalOver = getTotalOver();
    let slotDecreased = isSlotDecreased();


    useEffect(() => {
        if (downPress) {
            // setTime(0);
            let trumpetSound = new Audio(audio);
            if(cursor == -1){trumpetSound.play()}
            setBonusLost(0)

            if(selected !== undefined) {
                //if person takes less than set time
                if (nonZeroTime < selected.time) {
                    //increase subsequent slots that are under time (until they are back to their set times)
                    if (slotDecreased) {
                        for (let i = cursor + 1; i < todos.length; i++) {
                            let addedTime = todos[i].extra - (selected.time - nonZeroTime) / (todos.length - cursor - 1);
                            if(addedTime <= 0){
                                todos[i].extra = 0
                                setBonus(origBonus - (addedTime));
                            }
                            else{todos[i].extra = addedTime}
                        }
                    } else {
                        setBonus(bonusTime + (selected.time - nonZeroTime));
                    }
                    selected.time = nonZeroTime;
                }
            }

            if (cursor < todos.length) {
                setCursor(prevState =>
                    prevState < todos.length ? prevState + 1 : prevState)
            } else {
                setCursor(todos.length + 1);
            }
            setBefore(todos[cursor])
            setSelected(todos[cursor + 1]);
        }
    }, [downPress]);

    useEffect(() => {
        if (upPress) {
            if(cursor === 0){
                window.location.reload();
            }
            setBefore(todos[cursor-2])
            if(before !== undefined){
                let difference = before.initTime - before.time;
                //if slot before took less than designated time
                if(difference>0){
                    before.time = before.initTime;
                    if (slotDecreased) {
                        for (let i = cursor + 1; i < todos.length; i++) {
                            let addedTime = todos[i].extra + (difference) / (todos.length - cursor - 1);
                            if(addedTime <= 0){
                                todos[i].extra = 0
                                setBonus(origBonus + (addedTime));
                            }
                            else{todos[i].extra = addedTime}
                        }
                    } else {
                        setBonus(bonusTime - (difference));
                    }
                }
                }
            setCursor(prevState => (prevState > 0 ? prevState - 1 : prevState));
            setSelected(todos[cursor-1]);
        }
    }, [upPress]);


    const onDragEnd = ({source, destination}: DropResult) => {
        // Make sure we have a valid destination
        if (destination === undefined || destination === null ||
            destination.index < source.index && destination.index <= cursor) return null
        // Make sure we're actually moving the item
        if (destination.index === source.index) return null
        // Move the item within the list
        // Start by making a new list without the dragged item
        const newList = todos.filter((_: any, idx: number) => idx !== source.index)
        // Then insert the item at the right location
        newList.splice(destination.index, 0, todos[source.index])
        // Update the list
        setTodos(newList)
    }

    const fetchTodos = (): void => {
        getTodos()
            .then(({data: {todos}}: ITodo[] | any) => setTodos(todos))
            .catch((err: Error) => console.log(err))
    }
    useEffect(() => {
        fetchTodos();
    }, [])

    return (
        <DragDropContext onDragEnd={onDragEnd}>
            <main className='App'>
                <div className='test'>
                    <Droppable droppableId='col-1' isDropDisabled={false}>
                        {provided => {
                            const style = {
                                // height: (todoTime + totalOver)/(todoTime) + '%' ,
                                color: 'black',
                                ...provided.droppableProps,
                            };
                            return (
                                <ul className="characters"
                                    {...provided.droppableProps} ref={provided.innerRef} style={style}>
                                    {todos.map((todo: ITodo, index) => (
                                        <TodoItem
                                            key={todo._id}
                                            todo={todo}
                                            index={index}
                                            active={index === cursor}
                                            done={index <= cursor - 1}
                                            callbackFromParent2={timeCallback}
                                            percent={getPercent(todo)}
                                        />
                                    ))}
                                    {provided.placeholder}
                                </ul>)
                        }}
                    </Droppable>
                    <BonusItem
                        origBonus={origBonus} time={bonusTime} active={cursor === todos.length} done={cursor === todos.length + 1}
                        percent={bonusTime / (todoTime + bonusTime) * 100}/>
                </div>
                <button className="button"> Settings</button>
            </main>
        </DragDropContext>
    )
}

export default App
