import React, {useState} from 'react'
import { Draggable } from 'react-beautiful-dnd'
import {getLongestName, getTodos} from "../API";
import axios from "axios";
import {setMaxListeners} from "cluster";

type Props = TodoProps & {
    updateTodo: (todo: ITodo) => void
    deleteTodo: (_id: string) => void
    index: number
}
const Todo: React.FC<Props> = ({ todo, index }) => {
    const [longest, setLong] = useState<number>();
    const handleLongest = (): void => {
        getLongestName()
            .then(({ data:  longest  }) => setLong(longest))
            .catch((err: Error) => console.log(err))
    }

    return (
      <Draggable draggableId={todo._id} index={index} isDragDisabled={todo.status}>
          {provided => (
    <div className='Card' ref={provided.innerRef}
         {...provided.draggableProps}
         {...provided.dragHandleProps}>
      <div className="Card--text" >
        <div className='name' > {{longest: handleLongest()}}</div>
        <div className='description'>{todo.description}</div>
      </div>
    </div>
          )}
      </Draggable>
  )
}

export default Todo
